theme.custom_css_email:
.post .post-header .post-title {
  font-family: Baskerville, Georgia, serif;
  font-size: 40px;
  line-height: 42px;
  font-weight: normal;
  color: #000000;
}
.post .post-header .subtitle {
  font-family: Baskerville, 'Times New Roman', Georgia, serif;
  font-weight: normal;
  font-style: italic;
  font-size: 24px;
  line-height: 28px;
  color: #000000;
}
.post .post-meta .custom-css-email-post-author,
.post .post-meta .custom-css-email-post-author a {
  font-weight: 900 !important;
}
.markup p {
  font-family: Georgia, serif;
}
.typography .captioned-image-container-static figcaption {
  padding-left: 0;
  padding-right: 0;
  text-align: left;
  color: #808080;
  width: 100%;
}
.custom-css-email-avatar {
  display: none;
}

theme.custom_css_web:
.markup p {
  line-height: 1.6em;
}
.markup h2,
.markup h4,
.markup h6 {
  text-align: center;
}
